<script type="text/html" id="tmpl-seo-preview-twitter-large">

	<h4><?php esc_html_e( 'Twitter Share Preview', 'fusion' ); ?></h4>

	<div class="fm-wrapper fm-twitter_card_preview_type-wrapper">
		<div class="fm-item fm-twitter_card_preview_type fm-select">
			<div class="fm-label fm-label-twitter_card_preview_type">
				<label>Preview Type</label>
			</div>
			<select class="fm-element">
				<option value="wp-published-tweet" selected="">@Fusion’s tweet</option>
				<option value="user-shared-tweet">User Shared Tweet</option>
			</select>
			<div class="fm-item-description">
				<p>
					@Fusion’s tweet shows a preview of how the tweet will look via auto-publishing to our main Twitter account.
					<span class="link-show-more-text"> .... More</span>
				</p>
				<span class="fm-more-text">
					<p>User Shared Tweet shows a preview of how the tweet will look via the share buttons from our site, as well as provide possible recirculation options for the social team.</p>
				</span>
			</div>
		</div>
	</div>

	<div class="seo-preview-view twitter-card-preview-type wp-published-tweet">

		<div class="seo-preview-twitter-tweet">
			<div class="seo-preview-twitter-tweet-header">
				<img class="avatar" src="data:image/png;base64,"></img>
				<a class="account-group">
					<strong class="fullname">
						Fusion
					</strong>
					<span class="username">
						<del>@</del><b>{{{ data.twitter_user_name }}}</b>
					</span>
				</a>
			</div>
			<div class="seo-preview-twitter-tweet-text">{{{ data.twitter_share_text }}}</div>
		</div>

		<# if ( data.twitter_card_image ) { #>
		<div class="seo-preview-image" data-src="{{ data.twitter_card_image }}">
			<img src="{{ data.twitter_card_image }}" alt="" />
		</div>
		<# } #>
	</div>

	<div class="seo-preview-view twitter-card-preview-type user-shared-tweet" style="display: none">

		<div class="seo-preview-twitter-tweet">
			<div class="seo-preview-twitter-tweet-header">
				<img src="data:image/png;base64," alt=""/>
				<a class="account-group">
					<strong class="fullname">Users Name</strong>
					<span class="username">
						<del>@</del><b>handle</b>
					</span>
				</a>
			</div>
			<div class="seo-preview-twitter-tweet-text">{{{ data.twitter_share_text }}} via <span class="a">@{{{data.twitter_user_name}}}</span></div>
		</div>

		<div class="twitter-card-container">

			<# if ( data.twitter_card_image ) { #>
			<div class="seo-preview-image" data-src="{{ data.twitter_card_image }}">
				<img src="{{ data.twitter_card_image }}" alt="" />
			</div>
			<# } #>

			<div class="summary-card-container">
				<div class="summary-card-content">
					<div class="seo-preview-title">
						<span class="a">{{ data.twitter_card_title }}</span>
					</div>

					<div class="seo-preview-desc">
						{{ data.twitter_card_desc }}
					</div>

					<div class="seo-preview-domain-name">
						fusion.net
					</div>
				</div>
			</div>

		</div>

	</div>

</script>
